import React, { Component } from 'react';
import axios from 'axios';

export default class  Create extends Component {
  constructor(props) {
    super(props);
		

		this.onChangeNom = this.onChangeNom.bind(this);
		this.onChangeBirth = this.onChangeBirth.bind(this);
		this.onChangeFollowers = this.onChangeFollowers.bind(this);
		this.onChangeAlbums = this.onChangeAlbums.bind(this);

		this.onSubmit = this.onSubmit.bind(this);
		
		this.state = {
			Nom: '',
			Birth:'',
			Followers:'',
			Albums:''
	}
  }

  onChangeNom(e) {
    this.setState({Nom: e.target.value});
	}
	onChangeBirth(e) {
    this.setState({Birth: e.target.value});
	}
	onChangeFollowers(e) {
    this.setState({Followers: e.target.value});
	}
	onChangeAlbums(e) {
    this.setState({Albums: e.target.value});
  }

  onSubmit(e) {
		e.preventDefault();
		const artistes = {
			Nom: this.state.Nom,
			Birth: this.state.Birth,
			Followers: this.state.Followers, 
			Albums: this.state.Albums, 
		}
		axios.put('http://localhost:3001/artistes',artistes)
		.then(res => console.log(res.data));
		this.setState({
			Nom:'',
			Birth:'',
			Followers:'',
			Albums:''
		})
  }

  render() {
    return (
      <form >
				<div className ="form-group">
        <label>Name:</label>
					<input type="text" className="form-control" />
				</div>

				<div className ="form-group">
        <label>Birth:</label>
					<input type="text" className="form-control" />
				</div>

				<div className ="form-group">
        <label>Followers:</label>
					<input type="text" className="form-control" />
				</div>

				<div className ="form-group">
        <label>Albums:</label>
					<input type="text" className="form-control" />
				</div>

        <input type="submit" value="Submit" />
      </form>
    );
  }
}
